def pca(mat, num_components):
    '''
    mat [nxm]  - where n is samples and m is variables
    num_components (int) - number of principle components to keep

    returns mean, scaled principle components
    '''
    spec_sens = np.array(spec_senstivity)
    mean = np.mean(spec_sens, axis = 0)
    spec_sens_m = (spec_sens - mean)
    
    cov_mat = (spec_sens_m.T @ spec_sens_m) / (spec_sens.shape[0]-1)

    u, s, v = np.linalg.svd(cov_mat)

    pc_scaled = np.reshape(np.sqrt(s[0:num_components]), (num_components,1)) * v[0:num_components]

    return mean, pc_scaled

